// n8n Code Node - Parse Social Media JSON Data

// Get the JSON data from input
let rawData = $input.first().json.body.approvedCaption;

// If the data is a string with ```json markers, clean it up
if (typeof rawData === 'string') {
  // Remove ```json at the beginning and ``` at the end
  rawData = rawData.replace(/^```json\s*/, '').replace(/\s*```$/, '');
  // Parse the cleaned JSON string
  socialMediaData = JSON.parse(rawData);
} else {
  // Data is already an object
  socialMediaData = rawData;
}

// Transform the data into an array of items for n8n processing
const outputItems = [];

// Iterate through each platform
Object.keys(socialMediaData).forEach(platform => {
  const content = socialMediaData[platform];

  outputItems.push({
    json: {
      [platform]: {
        title: content.title,
        description: content.description,
        caption: content.caption,
        tags: content.tags
      }
    }
  });
});

// Return the processed items
return outputItems;
