// Get the first item (incoming JSON)
const data = $('Get Keywords').first().json.tasks[0].result;

// If result is not an array, throw error
if (!Array.isArray(data)) {
  throw new Error("Expected 'result' to be an array.");
}

// Limit to first 20 keywords
const sliced = data.slice(0, 20);

// Output only the latest monthly search info for each keyword
const output = [];

sliced.forEach(item => {
  let latest = null;

  if (Array.isArray(item.monthly_searches) && item.monthly_searches.length > 0) {
    // Sort monthly searches descending by year and month
    const sorted = item.monthly_searches.sort((a, b) => {
      if (b.year === a.year) return b.month - a.month;
      return b.year - a.year;
    });

    latest = sorted[0];
  }

  output.push({
    json: {
      keyword: item.keyword,
      location_code: item.location_code,
      language_code: item.language_code,
      cpc: item.cpc,
      competition: item.competition,
      competition_index: item.competition_index,
      year: latest?.year || null,
      month: latest?.month || null,
      search_volume: latest?.search_volume || item.search_volume || 0
    }
  });
});

return output;

