const rawInput = $('n8n Form Trigger').first().json['Target location(s)?'] || '';
const cleanedInput = rawInput
  .toLowerCase()
  .replace(/,/g, '')        // remove commas
  .replace(/\s+/g, ' ')     // normalize spaces
  .trim();                  // trim start/end

// Use regex to match common inputs
const locationMap = {
  "new york": { location_name: "New York, New York, United States", location_code: 1023199, state: "New York" },
  "los angeles": { location_name: "Los Angeles, California, United States", location_code: 1022781, state: "California" },
  "san francisco": { location_name: "San Francisco, California, United States", location_code: 1022780, state: "California" },
  "chicago": { location_name: "Chicago, Illinois, United States", location_code: 1022644, state: "Illinois" },
  "houston": { location_name: "Houston, Texas, United States", location_code: 1023127, state: "Texas" },
  "dallas": { location_name: "Dallas, Texas, United States", location_code: 1023124, state: "Texas" },
  "austin": { location_name: "Austin, Texas, United States", location_code: 1023121, state: "Texas" },
  "miami": { location_name: "Miami, Florida, United States", location_code: 1022356, state: "Florida" },
  "orlando": { location_name: "Orlando, Florida, United States", location_code: 1022358, state: "Florida" },
  "boston": { location_name: "Boston, Massachusetts, United States", location_code: 1023191, state: "Massachusetts" },
  "newton": { location_name: "Newton, Massachusetts, United States", location_code: 1023192, state: "Massachusetts" },
  "seattle": { location_name: "Seattle, Washington, United States", location_code: 1023215, state: "Washington" },
  "denver": { location_name: "Denver, Colorado, United States", location_code: 1022212, state: "Colorado" },
  "atlanta": { location_name: "Atlanta, Georgia, United States", location_code: 1022433, state: "Georgia" },
  "phoenix": { location_name: "Phoenix, Arizona, United States", location_code: 1022071, state: "Arizona" },
};

// Regex match from keys
let matchedKey = Object.keys(locationMap).find(key => {
  const regex = new RegExp(`^${key}(\\s+[a-z]+)?$`, 'i'); // allows optional state name
  return regex.test(cleanedInput);
});

if (!matchedKey) {
  // Try fallback (e.g., user typed city + state: "boston massachusetts")
  matchedKey = Object.keys(locationMap).find(key => cleanedInput.includes(key));
}

const result = locationMap[matchedKey];

if (!result) {
  throw new Error(`Unrecognized or unsupported location: "${rawInput}"`);
}

return [
  {
    json: {
      ...result,
      original_input: rawInput,
    }
  }
];
